import { useNavigate } from 'react-router-dom';

const Historial = () => {
  const navigate = useNavigate();
  const historial = JSON.parse(localStorage.getItem('analisis')) || [];

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">Historial de Análisis</h2>
      {historial.length === 0 ? (
        <p className="text-center">Aún no hay análisis guardados.</p>
      ) : (
        historial.reverse().map((item, idx) => (
          <div className="card mb-3 p-3 shadow-sm" key={idx}>
            <p><strong>Fecha:</strong> {item.fecha}</p>
            <p><strong>Texto:</strong> {item.texto}</p>
            <p><strong>Sentimiento:</strong> {item.sentimiento}</p>
            <p><strong>Polaridad:</strong> {item.polaridad}</p>
          </div>
        ))
      )}
      <div className="text-center mt-4">
        <button className="btn btn-secondary" onClick={() => navigate('/')}>
          Volver al Inicio
        </button>
      </div>
    </div>
  );
};

export default Historial;
